void scale_to_i(sdata* data, const int scale_to) {
  scalarf[scale_to](data);
}

void init_td_table() {
  // texture height and scale magnitude respectively.
  int th, s;
  float delta;
  for(th = 0; th < 513; th++) {
    for(s = 0; s < 201; s++) {
      if(s == 0)
        delta = 0;
      else
        delta = (float)th / (float)s;

      td_table[th][s] = (int)(delta * 1024);
    }
  }
}

int get_texture_delta(const int th, const int s) {
  return td_table[th][s];
}

void scale_to_1 (sdata* data) {
  unsigned int* dst = data->dst;
  unsigned int* src = data->src;
  const int dr = data->dr;
  const int dc = data->dc;
  const int tc = data->tc;
  const int td = data->td;
  const int ofst = data->ofst;
  const int hdist = data->hdist;
  int acc = 0;
  int zr = dr;
  dst[(0 + dr) * 320 + dc] = src[(((acc >> 10) + ofst) << 6) + tc];
  z_buffer_2d[dc][zr] = hdist;
}
